bg.style:
.resume-bg {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
}

personal.style:
.personal {
    display: flex;
    justify-content: start;
    align-items: center;
    gap: 20px;
    padding: 20px;
    box-sizing: border-box;
}
.avatar {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    overflow: hidden;
}
.info {
    display: flex;
    flex-direction: column;
    gap: 10px;
}
.name {
    font-size: 24px;
    font-weight: bold;
    color: #333;
}
.list {
    font-size: 16px;
    color: #666;
}

other.style:
.other {
    padding: 20px;
    box-sizing: border-box;
}
.title {
    font-size: 20px;
    font-weight: bold;
    color: #444;
    position: relative;
    margin-bottom: 10px;
}
.title::before {
    content: '';
    position: absolute;
    left: 0;
    bottom: -5px;
    width: 50px;
    height: 3px;
    background-color: #cce5ff;
}
.content {
    font-size: 16px;
    color: #555;
}
